import process from 'node:process';
import { d as defineCommand, b as legacyRootDirArgs, e as extendsArgs, f as envNameArgs, l as logLevelArgs, c as cwdArgs, a as dotEnvArgs } from '../shared/nuxi.GLC40kEn.mjs';
import { a as clearBuildDir } from '../shared/nuxi.CrvQqJcE.mjs';
import { l as loadKit } from '../shared/nuxi.BiWTNJI4.mjs';
import { l as logger } from '../shared/nuxi.DQjfoGl0.mjs';
import { r as resolve, a as relative } from '../shared/nuxi.Do0aYBCO.mjs';
import 'node:crypto';
import 'node:path';
import 'node:tty';
import 'node:url';
import 'node:fs';
import '../shared/nuxi.BLb4POFS.mjs';
import 'node:assert';
